        /// <summary>
        /// Converts an integer in the form of a string to an ASCII art string.
        /// </summary>
        /// <param name="numberToken">The number in type string.</param>
        /// <returns>The ASCII Art equivalent of the number.</returns>
        public static string ConvertNumber(string numberToken)
        {
            numberToken = numberToken.Replace("%", "        \n        \n        \n        \n        \n        ");
            numberToken = numberToken.Replace("0", "   ___  \n  / _ \\ \n | | | |\n | |_| |\n  \\___/ \n        ");
            numberToken = numberToken.Replace("1", "   __   \n  /  |  \n  |  |  \n  |  |  \n  |__|  \n        ");
            numberToken = numberToken.Replace("2", "  ____  \n |___ \\ \n   __) |\n  / __/ \n |_____|\n        ");
            numberToken = numberToken.Replace("3", "  _____ \n |___ / \n   |_ \\ \n  ___) |\n |____/ \n        ");
            numberToken = numberToken.Replace("4", " _  _   \n| || |  \n| || |_ \n|__   _|\n   |_|  \n        ");
            numberToken = numberToken.Replace("5", "  ____  \n | ___| \n |___ \\ \n  ___) |\n |____/ \n        ");
            numberToken = numberToken.Replace("6", "   __   \n  / /_  \n | '_ \\ \n | (_) |\n  \\___/ \n        ");
            numberToken = numberToken.Replace("7", "  _____ \n |___  |\n    / / \n   / /  \n  /_/   \n        ");
            numberToken = numberToken.Replace("8", "   ___  \n  ( _ ) \n  / _ \\ \n | (_) |\n  \\___/ \n        ");
            numberToken = numberToken.Replace("9", "   ___  \n  / _ \\ \n | (_) |\n  \\__, |\n    /_/ \n        ");

            return numberToken;
        }

